import React, { useContext, useState } from "react";
// import { useDispatchCart, useCart } from "./ContextReducer";
import { CartDispatchContext, CartStateContext } from './CreateContext';

export default function Card(props) {

  let data = useContext(CartStateContext);
  let dispatch = useContext(CartDispatchContext);
  let options = props.options;
  let priceOptions = Object.keys(options);

  const [quantity, setQuantity] = useState(1);
  const [size, setSize] = useState("");

  // const handleAddToCart = async () => {
  //   await dispatch({
  //     type: "ADD",
  //     id: props.foodItems._id,
  //     name: props.foodItems.name,
  //     price: props.finalPrice,
  //     quantity: quantity,
  //     size: size
  //   });
  //   console.log(data)
  // }


  return (
    <div>
      <div className="card mt-3" style={{ width: "18rem", maxHeight: "360px" }}>
        <img className="card-img-top" src={props.foodItems.img} style={{ height: "150px", objectFit: "fill" }} alt="Food card" />
        <div className="card-body">
          <h5 className="card-title">{props.foodItems.name}</h5>
          <div className="container w-100">
            <select className="m-2 h-100 bg-success rounded" onChange={(e) => setQuantity(e.target.value)} >
              {Array.from(Array(6), (e, i) => {
                return (
                  <option key={i + 1} value={i + 1}>
                    {i + 1}
                  </option>
                );
              })}
            </select>

            <select className="m-2 h-100 bg-success rounded" onChange={(e) => setSize(e.target.value)} >
              {priceOptions.map((data) => {
                return (
                  <option key={data} value={data} > {data} </option>
                )
              })}
            </select>

            <div className="d-inline h-100 fst-bold">Total Price</div>
          </div>
          <hr />
          {console.log(dispatch)}
          <button className={'btn btn-success justify-center ms-2'} onClick={async () => {
            await dispatch({
              type: "ADD",
              id: props.foodItems._id,
              name: props.foodItems.name,
              price: props.finalPrice,
              quantity: quantity,
              size: size
            });
            console.log(data)
          }} >
            Add to Cart</button>
        </div>
      </div>
    </div>
  );
}
