import React, { useEffect, useState } from "react";
import Navbar from "../Components/Navbar";
import Footer from "../Components/Footer";
import Card from "../Components/Card";

export default function Home() {

  const [foodCat, setFoodCat] = useState([]);
  const [foodItem, setFoodItem] = useState([]);
  const [search, setSearch] = useState('');

  const loadData = async () => {
    let response = await fetch('http://localhost:4000/api/foodData', {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      }
    });

    response = await response.json();
    // console.log(response[0],response[1])
    setFoodItem(response[0]);
    setFoodCat(response[1]);
  }

  useEffect(() => {
    loadData()
  }, [])

  return (
    <div>
      <div>
        <Navbar />
      </div>

      <div>
        <div class="container d-flex align-items-center justify-content-center" style={{ zIndex: "10" }}>
          <form className="d-flex justify-content-center">
            <input className="form-control m-2" type="search" placeholder="Search" aria-label="Search" value={search} onChange={(e) => {setSearch(e.target.value)}} />
            {/* <button className="btn text-success " type="submit">Search</button> */}
          </form>
        </div>
      </div>

      <div className="container">
        {
          foodCat !== []
            ? foodCat.map((data) => {
              return (
                <div className="row mb-3">
                  <div key={data._id} className="fs-2 m-3" >
                    {data.CategoryName}
                  </div>
                  <hr />
                  {foodItem !== []
                    ?
                    foodItem.filter((item) =>
                      item.CategoryName === data.CategoryName &&(item.name.toLowerCase().includes(search.toLocaleLowerCase()))) 
                      .map(filterItems => {
                        return (
                          <div key={filterItems._id} className="col-12 col-md-6 col-lg-3">
                            <Card foodItems = {filterItems}
                              options={filterItems.options[0]}
                            />
                          </div>
                        )
                      })
                    : <div>No such data found </div>}
                </div>
              )
            })
            : ""
        }
      </div>
      <div>
        <Footer />
      </div>
    </div>
  );
}
